import React, { Component } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import { Header, Loading, Footer, Nav } from './Common';
import { Home } from './Home';
import Quoting from './Quoting/Quoting';
import { Contact } from './Contact';
import { Gallery } from './Gallery';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            menuOption: 'quoting',
        };
    }

    renderContent() {
        if (this.state.menuOption) {
            switch (this.state.menuOption) {
                case 'index':
                    return (
                        <Home />
                    );
                    break;
                case 'quoting':
                    return (
                        <Quoting />
                    );
                    break;
                case 'contact':
                    return (
                        <Contact />
                    );
                    break;
                case 'gallery':
                    return (
                        <Gallery />
                    );
                    break;
            }
        }
    }

    render () {
        return (
            <div>
                <Header />
                <Nav />
                <Loading />
                {this.renderContent()}
                <Footer />
            </div>
        );
    }
}

export default App;
