import React, { Component } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import axios from 'axios';
import Build from './Build/Quoting';
import Bundles from './Bundles/Bundles';
import Mantain from './Mantain/Mantain';
import Form from './common/Form';
import './index.css';
import './Quoting.css';

class Quoting extends Component {
    constructor(props) {
        super(props);
        this.state = {
            menu: 0,
            name: '',
            phone: '',
            email: '',
            liters: '',
            session: '',
            preview: null,
            tanks: null,
            water: '1',
        };
    }

    componentDidMount() {
        //create session
        let session = (Math.random() + 1).toString(36);
        this.setState({ session });
        const headers = {
            "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK",
            "Content-Type": "application/json",
        };

        const body = {
            "uid": session,
        };

        axios.post(`http://api.mipecera.kimosolutions.com/api/session/create`, body, {headers}).then(response  => response.data);
        axios.get(`http://api.mipecera.kimosolutions.com/api/materials/tanks`, {headers}).then(response  => this.setState({ tanks: response.data }));
    }

    onChangeWater = e => {
        this.setState({ water: e.target.value });
    }

    onChangeLiters = e => {
        this.setState({ liters: e.target.value });
    }

    onChangeName = e => {
        this.setState({ name: e.target.value });
    }

    onChangeEmail = e => {
        this.setState({ email: e.target.value });
    }

    onChangePhone = e => {
        this.setState({ phone: e.target.value });
    }

    async updateData() {
        if (this.state.water != '' && this.state.liters != '') {
            if (this.state.tanks.filter(tank => tank.capacity > this.state.liters)[0]) {
                const headers = {
                    "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK",
                    "Content-Type": "application/json",
                };

                const body_1 = {
                    "uid": this.state.session,
                    "tank_id": await this.state.tanks.filter(tank => tank.capacity > this.state.liters)[0].id,
                    "water_id": this.state.water
                };

                if (this.state.name != '' && this.state.email != '' && this.state.phone != '') {
                    const body_2 = {
                        "uid": this.state.session,
                        "first_name": this.state.name.split(" ")[0],
                        "last_name": this.state.name.split(" ")[1],
                        "email": this.state.email,
                        "phone_number": this.state.phone
                    };

                    await axios.put(`http://api.mipecera.kimosolutions.com/api/session/update`, body_1, {headers}).then(response  => response.data);
                    await axios.post(`http://api.mipecera.kimosolutions.com/api/client_info`, body_2, {headers}).then(response  => response.data);

                    this.getPreview();
                }
                else {
                    alert('Debes completar todos los campos.');
                }
            } else {
                this.setState({ preview: {maintenance: 'Debido a la cantidad de litros de tú pecera, será mejor que nos contactes dejando tu mail en la Sección de Contacto para darte la mejor solución.'}, menu: 5 })
            }
        }
        else {
            alert('Debes completar todos los campos.');
        }
    }

    async getPreview() {
        const headers = {
            "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK",
            "Content-Type": "application/json",
        };
        await axios.get(`http://api.mipecera.kimosolutions.com/api/session/show/${this.state.session}`, {headers}).then(response  => this.setState({ preview: response.data[0], menu: 5 }));
    }

    renderContent() {
        switch(this.state.menu) {
            case 0:
                return (
                    <div className="container-fluid" style={{ height: 300 }}>
                        <div className="container">
                            <div className="section-header">
                                <div className="quoting-buttons" onClick={() => this.setState({ menu: 1 })}>
                                    Tengo Mi Pecera
                                </div>
                                <div className="quoting-buttons" onClick={() => this.setState({ menu: 2 })}>
                                    Armar Mi Pecera
                                </div>
                            </div>
                        </div>
                    </div>
                );
                break;
            case 1:
                return (
                    <div>
                        <div className="back-button" onClick={() => this.setState({ menu: 0, water: '', liters: '' })}>
                            <i className="fa fa-arrow-left"></i> Regresar
                        </div>
                        <div>
                            <Mantain ref='child' water={this.state.water} liters={this.state.liters} onChangeWater={this.onChangeWater} onChangeLiters={this.onChangeLiters}/>
                            <Form
                                name={this.state.name}
                                email={this.state.email}
                                phone={this.state.phone}
                                onChangeName={this.onChangeName}
                                onChangeEmail={this.onChangeEmail}
                                onChangePhone={this.onChangePhone}
                            />
                            <div className="form-group">
                                <input type="submit" title="Cotizar Mantenimiento"  value="Enviar Mensaje" id="btn_submit" name="post" onClick={() => this.updateData()} />
                            </div>
                        </div>
                    </div>
                );
                break;
            case 2:
                return (
                    <div>
                        <div className="back-button" onClick={() => this.setState({ menu: 0 })}>
                            <i className="fa fa-arrow-left"></i> Regresar
                        </div>
                        <div className="container-fluid no-padding">
                            <div className="container">
                                <div className="section-header">
                                    <div className="quoting-buttons" onClick={() => this.setState({ menu: 3 })}>
                                        Paquetes
                                    </div>
                                    <div className="quoting-buttons" onClick={() => this.setState({ menu: 4 })}>
                                        Armar
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                );
                break;
            case 3:
                return (
                    <div>
                        <div className="back-button" onClick={() => this.setState({ menu: 2 })}>
                            <i className="fa fa-arrow-left"></i> Regresar
                        </div>
                        <div>
                            <Bundles session={this.state.session}/>
                        </div>
                    </div>
                );
                break;
            case 4:
                return (
                    <div>
                        <div className="back-button" onClick={() => this.setState({ menu: 2 })}>
                            <i className="fa fa-arrow-left"></i> Regresar
                        </div>
                        <div>
                            <Build session={this.state.session}/>
                        </div>
                    </div>
                );
                break;
            case 5:
                return (
                    <div>
                        <div className="back-button" onClick={() => this.setState({ menu: 1 })}>
                            <i className="fa fa-arrow-left"></i> Regresar
                        </div>
                        <div>
                            <p>Mantenimiento</p>
                            <p>{this.state.preview.maintenance}</p>
                        </div>
                    </div>
                );
                break;
        }
    }

    render () {
        return (
            <div className="background-img">
                <main className="site-main site-main-react page-spacing container" style={{ backgrounColor: 'rgba(255,255,255,0.8)' }}>
                    {this.renderContent()}
                </main>
            </div>
        );
    }
}

export default Quoting;
