import React, { Component } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';

class Quoting extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [1,2,3],
            step: 0,
        };
    }

    renderStep() {
        switch (this.state.step) {
            case 0:
                if (this.state.items) {
                    this.state.items.map(item => {
                        return (
                            <p>Hola 1</p>
                        );
                    });
                }
                return null;
                break;
            case 1:
                return (
                    <p>Hola 2</p>
                );
                break;
            case 2:
                return (
                    <p>Hola 3</p>
                );
                break;
        }
    }

    render () {
        return (
            <main className="site-main page-spacing">
                <div className="container-fluid no-padding">
                    <div className="section-padding"></div>
                    <div className="container">
                        <div className="row">
                            <div className="col-md-3">
                                <div className="section-header">
                					<h3>Elige el producto:</h3>
                				</div>
                                {this.renderStep()}
                            </div>
                            <div className="col-md-9">
                                <div className="section-header">
                                    <Tabs selectedIndex={this.state.step} onSelect={step => this.setState({ step })}>
                                        <TabList>
                                            <Tab>1</Tab>
                                            <Tab>2</Tab>
                                            <Tab>3</Tab>
                                        </TabList>
                                    </Tabs>
                                </div>
                                <div className="section-padding"></div>
                                <div>
                                    <canvas>
                                        <p>Max</p>
                                    </canvas>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="section-padding"></div>
                </div>
            </main>
        );
    }
}

export { Quoting };
