import React, { Component } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import { Header, Loading, Footer } from './Common';
import { Home } from './Home';
import Quoting from './Quoting';
import { Contact } from './Contact';
import { Gallery } from './Gallery';
import './index.css';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            menuOption: 0,
        };
    }

    renderContent() {
        switch (this.state.menuOption) {
            case 0:
                return (
                    <Home />
                );
            case 1:
                return (
                    <Quoting />
                );
            case 2:
                return (
                    <Gallery />
                );
            case 3:
                return (
                    <Contact />
                );
            default:
                break;
        }
    }

    render () {
        return (
            <div>
                <Header />
                <header className="header container-fluid no-padding">
                    <div className="menu-block container-fluid no-pdding">
                        <div className="container">
                            <nav className="navbar ow-navigation">
                                <div className="navbar-header">
                                    <button type="button" className="navbar-toggle collapsed" data-toggle="collapse" data-target="#navbar" aria-expanded="false" aria-controls="navbar">
                                        <span className="sr-only">Toggle navigation</span>
                                        <span className="icon-bar"></span>
                                        <span className="icon-bar"></span>
                                        <span className="icon-bar"></span>
                                    </button>
                                    <a className="navbar-brand" href="index.html" title="Logo"><img src={require('./Common/images/logo.png')} alt="Logo" style={{ width: 200, height: 80, position: 'absolute' }} /></a>
                                </div>
                                <div id="navbar" className="navbar-collapse collapse">
                                    <ul className="nav navbar-nav">
                                        <Tabs className="nav-tabContainer" selectedTabClassName="nav-tab-selected" selectedIndex={this.state.menuOption} onSelect={(index) => this.setState({ menuOption: index })}>
                                            <TabList>
                                                <Tab className="nav-tabs-react"><a>INICIO</a></Tab>
                                                <Tab className="nav-tabs-react"><a>COTIZADOR</a></Tab>
                                                <Tab className="nav-tabs-react"><a>GALERÍA</a></Tab>
                                                <Tab className="nav-tabs-react"><a>CONTACTO</a></Tab>
                                            </TabList>
                                            <TabPanel>
                                            </TabPanel>
                                            <TabPanel>
                                            </TabPanel>
                                            <TabPanel>
                                            </TabPanel>
                                            <TabPanel>
                                            </TabPanel>
                                        </Tabs>
                                    </ul>
                                </div>
                            </nav>
                        </div>
                    </div>
                </header>
                <Loading />
                {this.renderContent()}
                <Footer />
            </div>
        );
    }
}

export default App;
