import React, { Component } from 'react';
import '../../Contact/Contact.css';

class Mantain extends Component {
    constructor(props) {
        super(props);
        this.state = {
            liters: null,
            water: null,
        }
    }

    getState() {
        return this.state;
    }

    handleWater(text) {
        this.setState({ water: text });
    }

    handleLiters(text) {
        this.setState({ liters: text });
    }

    render() {
        return (
            <div>
                <form className="contact-form">
                    <div className="row">
                        <div className="form-group col-md-6">
                            <input type="text" name="contact-name" className="form-control" id="input_name" placeholder="Tipo de Agua *" value={this.state.water} onChange={this.handleWater.bind(this)} required/>
                        </div>
                        <div className="form-group col-md-6">
                            <input type="email" name="contact-email" className="form-control" id="input_email" value={this.state.liters} onChange={this.handleLiters.bind(this)} placeholder="Litros de la Pecera *" required/>
                        </div>
                    </div>
                </form>
                <div className="section-padding"></div>
            </div>
        );
    }
}

export default Mantain;
