import React, { Component } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import './quote.css';

class Quoting extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [1,2,3],
            step: 0,
        };
    }

    renderStep() {
        switch (this.state.step) {
            case 0:
                return (
                    <p>Hola 1</p>
                );
                break;
            case 1:
                if (this.state.items) {
                    this.state.items.map(item => {
                        return (
                            <p>Hola 2</p>
                        );
                    });
                }
                return null;
                break;
            case 2:
                if (this.state.items) {
                    this.state.items.map(item => {
                        return (
                            <p>Hola 3</p>
                        );
                    });
                }
                return null;
                break;
            default:
                break;
        }
    }

    render () {
        return (
            <main className="site-main page-spacing">
                <div className="container-fluid no-padding">
                    <div className="section-padding"></div>
                    <div className="container">
                        <div className="row">
                            <div className="col-md-3">
                                <div className="section-header">
                					<h3>Elige el producto:</h3>
                				</div>
                                <div>
                                    {this.renderStep()}
                                </div>
                            </div>
                            <div className="col-md-9">
                                <div className="section-header">
                                    <Tabs className="tabContainer" selectedIndex={this.state.step} onSelect={step => this.setState({ step })}>
                                        <TabList>
                                            <Tab className="tabs">1</Tab>
                                            <Tab className="tabs">2</Tab>
                                            <Tab className="tabs">3</Tab>
                                            <Tab className="tabs">4</Tab>
                                        </TabList>
                                        <TabPanel>
                                        </TabPanel>
                                        <TabPanel>
                                        </TabPanel>
                                        <TabPanel>
                                        </TabPanel>
                                        <TabPanel>
                                        </TabPanel>
                                    </Tabs>
                                </div>
                                <div>
                                    <div>
                                        <img src={require('./images/slider/8.jpg')} />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="section-padding"></div>
                </div>
            </main>
        );
    }
}

export { Quoting };
