import React, { Component } from 'react';
import axios from 'axios';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import Form from '../common/Form';
import './quote.css';

class Build extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fishBowls: [],
            selectedFishBowl: null,
            water: [],
            selectedWater: null,
            floor: [],
            selectedFloor: null,
            decorationBundle: [],
            selectedDecorationBundle: null,
            ornamentBundle: [],
            selectedOrnamentBundle: null,
            fishBundle: [],
            selectedFishBundle: null,
            preview: null,
            name: '',
            email: '',
            phone: '',
            step: 0,
        };
    }

    componentDidMount() {
        //get fishBowls
        axios.get(`http://api-mipecera.kimosolutions.com/api/materials/tanks`, {
            headers: {
                "Content-Type": "application/json",
                "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
            }
        }).then(response  => response.data.map(fishBowls => this.setState({ fishBowls: [...this.state.fishBowls, fishBowls] })));
    }

    componentWillMount() {
        let session = (Math.random() + 1).toString(36);
        this.setState({ session });
        const headers = {
            "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK",
            "Content-Type": "application/json",
        };

        const body = {
            "uid": session,
        };

        axios.post(`http://api-mipecera.kimosolutions.com/api/session/create`, body, {headers}).then(response  => response.data);
    }

    onChangeName = e => {
        this.setState({ name: e.target.value });
    }

    onChangeEmail = e => {
        this.setState({ email: e.target.value });
    }

    onChangePhone = e => {
        this.setState({ phone: e.target.value });
    }

    updateSession1(index) {
        const headers = {
                "Content-Type": "application/json",
                "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
        };

        let body;

        if (index == 0) {
            body = {
                "uid": this.state.session,
                "water_id": null,
                "package": null,
                "floor_id": null,
                "decoration_id": null,
                "ornament_id": null,
                "fish_id": null,
            };
        }
        else if (index == 1) {
            body = {
                "uid": this.state.session,
                "package": null,
                "floor_id": null,
                "decoration_id": null,
                "ornament_id": null,
                "fish_id": null,
            };
        }
        else if (index == 2) {
            body = {
                "uid": this.state.session,
                "package": null,
                "decoration_id": null,
                "ornament_id": null,
                "fish_id": null,
            };
        }
        else if (index == 3) {
            body = {
                "uid": this.state.session,
                "package": null,
                "ornament_id": null,
                "fish_id": null,
            };
        }
        else if (index == 4) {
            body = {
                "uid": this.state.session,
                "package": null,
                "fish_id": null,
            };
        }

        axios.put(`http://api-mipecera.kimosolutions.com/api/session/update`, body, {headers}).then(response  => response.data);
    }

    changeSelectedFishBowl(item) {
        const headers = {
                "Content-Type": "application/json",
                "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
        };
