import React, { Component } from 'react';
import axios from 'axios';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import './quote.css';

class Quoting extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fishBowls: [],
            selectedFishBowl: null,
            water: [],
            selectedWater: null,
            floor: [],
            selectedFloor: null,
            product: 'tu pecera',
            items: ['hola','hola2','hola3'],
            step: 0,
        };
    }

    componentWillMount() {
        //get fishBowls
        axios.get(`http://mipecera.kimosolutions.com/api/step/1`, {
            headers: {
                "Content-Type": "application/json",
                "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
            }
        }).then(response  => response.data.map(fishBowls => this.setState({ fishBowls: [...this.state.fishBowls, fishBowls] })));

        //get water
        axios.get(`http://mipecera.kimosolutions.com/api/step/2`, {
            headers: {
                "Content-Type": "application/json",
                "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
            }
        }).then(response  => response.data.map(water => this.setState({ water: [...this.state.water, water] })));

        //get floor
        axios.get(`http://mipecera.kimosolutions.com/api/step/3`, {
            headers: {
                "Content-Type": "application/json",
                "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
            }
        }).then(response  => response.data.map(floor => this.setState({ floor: [...this.state.floor, floor] })));
    }

    changeProduct() {
        switch (this.state.step) {
            case 0:
                this.setState({ product: 'tu pecera' });
                break;
            case 1:
                this.setState({ product: 'el agua' });
                break;
            case 2:
                this.setState({ product: 'el suelo' });
                break;
            case 3:
                this.setState({ product: 'las plantas' });
                break;
            default:
                break;
        }
    }

    changeSelectedFishBowl(item) {
        if (this.state.selectedFishBowl) {
            if (this.state.selectedFishBowl !== item) {
                this.setState({ selectedFishBowl: item });
            }
        }
        this.setState({ selectedFishBowl: item });
    }

    renderStep() {
        switch (this.state.step) {
            case 0:
                if (this.state.fishBowls) {
                    return this.state.fishBowls.map(item => {
                        return (
                            <div className="item" onClick={() => this.changeSelectedFishBowl(item)}>
                                <div className="row">
                                    <div className="col-md-5">
                                        <img src={item.image} alt={item.code}/>
                                    </div>
                                    <div className="col-md-7">
                                        <p><strong>Capacidad:</strong> {item.capacity} cm</p>
                                        <p><strong>Altura:</strong> {item.height} cm</p>
                                        <p><strong>Largo:</strong> {item.width} cm</p>
                                        <p><strong>Ancho:</strong> {item.depth} cm</p>
                                    </div>
                                </div>
                            </div>
                        );
                    });
                }
                return null;
                break;
            case 1:
                let water = this.state.water;
                return (
                    <div>
                        <div className="item" onClick={() => this.changeSelectedWater(water[0].type)}>
                            <div className="row">
                                <div className="col-md-6">
                                    <img src={water[0].image['EA-80']} alt={water[0].type}/>
                                </div>
                                <div className="col-md-6">
                                    <p><strong>{water[0].type}</strong></p>
                                </div>
                            </div>
                        </div>
                        <div className="item" onClick={() => this.changeSelectedWater(water[1].type)}>
                            <div className="row">
                                <div className="col-md-6">
                                    <img src={water[1].image['EA-80']} alt={water[1].type}/>
                                </div>
                                <div className="col-md-6">
                                    <p><strong>{water[1].type}</strong></p>
                                </div>
                            </div>
                        </div>
                    </div>
                );
                break;
            case 2:
                if (this.state.floor) {
                    return this.state.floor.map(item => {
                        return (
                            <div className="item">
                                <div className="row">
                                    <div className="col-md-6">
                                        <img src={item.image['EA-100']} alt={item.id}/>
                                    </div>
                                    <div className="col-md-6">
                                        <p><strong>{item.name}</strong></p>
                                    </div>
                                </div>
                            </div>
                        );
                    });
                }
                return null;
                break;
            case 3:
                if (this.state.items) {
                    return this.state.items.map(item => {
                        return (
                            <p>{item}</p>
                        );
                    });
                }
                return null;
                break;
            default:
                break;
        }
    }

    renderProducts() {
        if (this.state.selectedWater && this.state.selectedFishBowl && this.state.selectedFloor) {
            return (
                <div>
                    <img className="selectedProduct" src={this.state.selectedWater.image[this.state.selectedFishBowl.code]} />
                    <img className="selectedProduct" src={this.state.selectedFishBowl.image} />
                    <img className="selectedProduct" src={this.state.selectedFloor.image[this.state.selectedFishBowl.code]} />
                </div>
            );
        }
        else if (this.state.selectedWater && this.state.selectedFishBowl) {
            return (
                <div>
                    <img className="selectedProduct" src={this.state.selectedWater.image[this.state.selectedFishBowl.code]} />
                    <img className="selectedProduct" src={this.state.selectedFishBowl.image} />
                </div>
            );
        }
        else if (this.state.selectedFishBowl) {
            return(
                <img className="selectedProduct" src={this.state.selectedFishBowl.image} />
            );
        }
        return (
            <p>Selecciona para visualizar.</p>
        );
    }

    render () {
        return (
            <main className="site-main page-spacing">
                <div className="container-fluid no-padding">
                    <div className="container">
                        <div className="section-header">
                            <Tabs className="tabContainer" selectedTabClassName="tab-selected" selectedIndex={this.state.step} onSelect={step => { this.setState({ step }); this.changeProduct(); }}>
                                <TabList>
                                    <Tab className="tabs">1</Tab>
                                    <Tab className="tabs">2</Tab>
