import React, { Component } from 'react';
import axios from 'axios';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import './quote.css';

class Bundles extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fishBowls: [],
            selectedFishBowl: null,
            water: [],
            selectedWater: null,
            bundle: [],
            selectedBundle: null,
            preview: null,
            step: 0,
        };
    }

    componentWillMount() {
        //get fishBowls
        axios.get(`http://api.mipecera.kimosolutions.com/api/materials/tanks`, {
            headers: {
                "Content-Type": "application/json",
                "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
            }
        }).then(response  => response.data.map(fishBowls => this.setState({ fishBowls: [...this.state.fishBowls, fishBowls] })));
    }

    changeSelectedFishBowl = async item => {
        if (this.state.selectedFishBowl) {
            if (this.state.selectedFishBowl !== item) {
                const headers = {
                        "Content-Type": "application/json",
                        "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
                };
                const body = {
                    "uid": this.state.session,
                    "tank_id": item.id,
                };
                await axios.put(`http://api.mipecera.kimosolutions.com/api/session/update`, body, {headers}).then(response  => response.data);
                this.setState({ selectedFishBowl: item });
            }
        }
        this.setState({ selectedFishBowl: item });
    }

    changeSelectedWater = async item => {
        if (this.state.selectedWater) {
            if (this.state.selectedWater !== item) {
                const headers = {
                        "Content-Type": "application/json",
                        "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
                };
                const body = {
                    "uid": this.state.session,
                    "water_id": item.id,
                };
                await axios.put(`http://api.mipecera.kimosolutions.com/api/session/update`, body, {headers}).then(response  => response.data);
                this.setState({ selectedWater: item });
            }
        }
        this.setState({ selectedWater: item });
    }

    changeSelectedBundle = async item => {
        if (this.state.selectedBundle) {
            if (this.state.selectedBundle !== item) {
                const headers = {
                        "Content-Type": "application/json",
                        "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
                };
                const body = {
                    "uid": this.state.session,
                    "package_id": item.id,
                };
                await axios.put(`http://api.mipecera.kimosolutions.com/api/session/update`, body, {headers}).then(response  => response.data);
                this.setState({ selectedBundle: item });
            }
        }
        this.setState({ selectedBundle: item });
    }

    checkIfSelected() {
        switch (this.state.step) {
            case 0:
                if (this.state.selectedFishBowl) {
                    //get fishBowls
                    axios.get(`http://api.mipecera.kimosolutions.com/api/materials/basic_materials/${this.props.session}/water`, {
                        headers: {
                            "Content-Type": "application/json",
                            "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
                        }
                    }).then(response  => this.setState({ step: this.state.step + 1, water: response.data }) );
                }
                else {
                    alert('Debes seleccionar una pecera para continuar.');
                }
                break;
            case 1:
                if (this.state.selectedWater) {
                    //get fishBowls
                    axios.get(`http://api.mipecera.kimosolutions.com/api/packages/${this.props.session}`, {
                        headers: {
                            "Content-Type": "application/json",
                            "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
                        }
                    }).then(response  => this.setState({ step: this.state.step + 1, bundle: response.data }) );
                }
                else {
                    alert('Debes seleccionar un tipo de agua para continuar.');
                }
                break;
            case 2:
                if (this.state.selectedFloor) {
                    //get fishBowls
                    axios.get(`http://api.mipecera.kimosolutions.com/api/session/show/${this.props.session}`, {
                        headers: {
                            "Content-Type": "application/json",
                            "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
                        }
                    }).then(response  => this.setState({ step: this.state.step + 1, preview: response.data }) );
                }
                else {
                    alert('Debes seleccionar un paquete para continuar.');
                }
                break;
        }
    }

    returnStep() {
        switch (this.state.step) {
            case 1:
                if (this.state.selectedWater) {
                    this.setState({ step: this.state.step - 1, selectedWater: null });
                }
                else {
                    this.setState({ step: this.state.step - 1 });
                }
                break;
            case 2:
                if (this.state.selectedBundle) {
                    this.setState({ step: this.state.step - 1, selectedBundle: null });
                }
                else {
                    this.setState({ step: this.state.step - 1 });
                }
                break;
        }
    }

    renderStep() {
        switch (this.state.step) {
            case 0:
                if (this.state.fishBowls) {
                    return this.state.fishBowls.map(item => {
                        return (
                            <div className="item-product" onClick={() => this.changeSelectedFishBowl(item)}>
                                <p className="item-title"><strong>{item.code}</strong></p>
                                <div className="row">
                                    <div className="col-md-5">
                                        <img src={item.img} alt={item.code}/>
                                    </div>
                                    <div className="col-md-7">
                                        <p><strong>Capacidad:</strong> {item.capacity} cm</p>
                                        <p><strong>Altura:</strong> {item.height} cm</p>
                                        <p><strong>Largo:</strong> {item.width} cm</p>
                                        <p><strong>Ancho:</strong> {item.depth} cm</p>
                                    </div>
                                </div>
                            </div>
                        );
                    });
                }
                return null;
                break;
            case 1:
                return this.state.water.map(item => {
                    return (
                        <div className="item-product" onClick={() => this.changeSelectedWater(item)}>
                            <p className="item-title"><strong>{item.type}</strong></p>
                            <div className="row">
                                <div className="col-md-6">
                                    <img src={item.img} alt={item.type}/>
                                </div>
                                <div className="col-md-6">
                                    <p><strong>{item.type}</strong></p>
                                </div>
                            </div>
                        </div>
                    );
                });
                break;
            case 2:
                if (this.state.bundle) {
                    return this.state.bundle.map(item => {
                        return (
                            <div className="item-product" onClick={() => this.changeSelectedFloor(item)}>
                                <p className="item-title"><strong>{item.name}</strong></p>
                                <div className="row">
                                    <div className="col-md-6">
                                        <img src={item.img} alt={item.id}/>
                                    </div>
                                    <div className="col-md-6">
                                        <p><strong>{item.name}</strong></p>
                                    </div>
                                </div>
                            </div>
                        );
                    });
                }
                return null;
                break;
            default:
                break;
        }
    }

    renderLabel() {
        switch (this.state.step) {
            case 0:
                return 'ELIGE TU PECERA';
                break;
            case 1:
                return 'ELIGE EL AGUA';
                break;
            case 2:
                return 'ELIGE UN PAQUETE';
                break;
            default:
                break;
        }
    }

    renderProducts() {
        // <img className="selectedProduct" src={this.state.selectedWater.image[this.state.selectedFishBowl.code]} />
        if (this.state.selectedWater && this.state.selectedFishBowl && this.state.selectedBundle) {
            return (
                <div>
                    <img className="selectedProduct" src={this.state.selectedWater.img} />
                    <img className="selectedProduct" src={this.state.selectedFishBowl.img} />
                    <img className="selectedProduct" src={this.state.selectedBundle.img} />
                </div>
            );
        }
        else if (this.state.selectedWater && this.state.selectedFishBowl) {
            return (
                <div>
                    <img className="selectedProduct" src="#" />
                    <img className="selectedProduct" src={this.state.selectedFishBowl.img} />
                </div>
            );
        }
        else if (this.state.selectedFishBowl) {
            return(
                <img className="selectedProduct" src={this.state.selectedFishBowl.img} />
            );
        }
        return (
            <p className="non-selected-label">Selecciona para visualizar</p>
        );
    }

    renderButtons() {
        if (this.state.step == 0) {
            return (
                <div className="nav-buttons">
                    <button id="next-button" className="button" onClick={() => this.checkIfSelected()}>Siguiente <i className="fa fa-arrow-right"></i></button>
                </div>
            );
        }
        else if (this.state.step > 0) {
            return (
                <div className="nav-buttons">
                    <button id="back-button" className="button" onClick={() => this.returnStep()}><i className="fa fa-arrow-left"></i> Regresar</button>
                    <button id="next-button" className="button" onClick={() => this.checkIfSelected()}>Siguiente <i className="fa fa-arrow-right"></i></button>
                </div>
            );
        }
        else if (this.state.step == 2) {
            return (
                <div className="nav-buttons">
                    <button id="back-button" className="button" onClick={() => this.returnStep()}><i className="fa fa-arrow-left"></i> Regresar</button>
                </div>
            );
        }
    }

    render () {
        return (
            <main className="site-main site-main-react page-spacing">
                <div className="container-fluid no-padding">
                    <div className="container">
                        <div className="section-header">
                            <Tabs className="tabContainer" selectedTabClassName="tab-selected" selectedIndex={this.state.step}>
                                <TabList>
                                    <Tab className="tabs">1</Tab>
                                    <Tab className="tabs">2</Tab>
                                    <Tab className="tabs">3</Tab>
                                    <Tab className="tabs">4</Tab>
                                    <Tab className="tabs">5</Tab>
                                </TabList>
                                <TabPanel>
                                </TabPanel>
                                <TabPanel>
                                </TabPanel>
                                <TabPanel>
                                </TabPanel>
                                <TabPanel>
                                </TabPanel>
                                <TabPanel>
                                </TabPanel>
                            </Tabs>
                        </div>
                        <div className="row">
                            <div className="col-md-4">
                                <div className="itemContainer">
                                    <h2>{this.renderLabel()}:</h2>
                                    <div>
                                        {this.renderStep()}
                                    </div>
                                </div>
                            </div>
                            <div className="col-md-8">
                                <div className="productContainer">
                                    {this.renderProducts()}
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="section-padding"></div>
                </div>
                <div className="container-fluid no-padding">
                    <div className="container">
                        <div className="section-header">
                            <div className="nav-buttons">
                                {this.renderButtons()}
                            </div>
                        </div>
                    </div>
                </div>
            </main>
        );
    }
}

export default Bundles;
