import React, { Component } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import Build from './Build/Quoting';
import Mantain from './Mantain/Mantain';
import Form from './common/Form';
import './index.css';
import './Quoting.css';

class Quoting extends Component {
    constructor(props) {
        super(props);
        this.state = {
            menu: 0,
            name: '',
            phone: '',
            email: '',
            water: null,
            liters: null,
        };
    }

    componentDidMount() {
        //get fishBowls
        axios.post(`http://api.mipecera.kimosolutions.com/api/session/create`, {
            headers: {
                "Content-Type": "application/json",
                "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
            }
        }).then(response  => response.data);
    }

    onChangeWater = e => {
        this.setState({ water: e.target.value });
    }

    onChangeLiters = e => {
        this.setState({ liters: e.target.value });
    }

    callChildrenMethod() {
        console.log(this.state);
    }

    renderContent() {
        switch(this.state.menu) {
            case 0:
                return (
                    <div className="container-fluid no-padding">
                        <div className="container">
                            <div className="section-header">
                                <div className="quoting-buttons" onClick={() => this.setState({ menu: 1 })}>
                                    Tengo Mi Pecera
                                </div>
                                <div className="quoting-buttons" onClick={() => this.setState({ menu: 2 })}>
                                    Armar Mi Pecera
                                </div>
                            </div>
                        </div>
                    </div>
                );
                break;
            case 1:
                return (
                    <div>
                        <div className="back-button" onClick={() => this.setState({ menu: 0 })}>
                            <i className="fa fa-arrow-left"></i> Regresar
                        </div>
                        <div>
                            <Mantain ref='child' water={this.state.water} liters={this.state.liters} onChangeWater={this.onChangeWater} onChangeLiters={this.onChangeLiters}/>
                            <Form />
                            <div className="form-group">
                                <input type="submit" title="Enviar Mensaje"  value="Enviar Mensaje" id="btn_submit" name="post" onClick={() => this.callChildrenMethod()} />
                            </div>
                        </div>
                    </div>
                );
                break;
            case 2:
                return (
                    <div>
                        <div className="back-button" onClick={() => this.setState({ menu: 0 })}>
                            <i className="fa fa-arrow-left"></i> Regresar
                        </div>
                        <div className="container-fluid no-padding">
                            <div className="container">
                                <div className="section-header">
                                    <div className="quoting-buttons" onClick={() => this.setState({ menu: 3 })}>
                                        Paquetes
                                    </div>
                                    <div className="quoting-buttons" onClick={() => this.setState({ menu: 4 })}>
                                        Armar
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                );
                break;
            case 3:
                return (
                    <div>
                        <div className="back-button" onClick={() => this.setState({ menu: 2 })}>
                            <i className="fa fa-arrow-left"></i> Regresar
                        </div>
                        <div>
                            Hola!
                        </div>
                    </div>
                );
                break;
            case 4:
                return (
                    <div>
                        <div className="back-button" onClick={() => this.setState({ menu: 2 })}>
                            <i className="fa fa-arrow-left"></i> Regresar
                        </div>
                        <div>
                            <Build />
                        </div>
                    </div>
                );
                break;
        }
    }

    render () {
        return (
            <main className="site-main site-main-react page-spacing">
                {this.renderContent()}
            </main>
        );
    }
}

export default Quoting;
