import React, { Component } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import './quote.css';

class Quoting extends Component {
    constructor(props) {
        super(props);
        this.state = {
            product: 'tu pecera',
            items: ['hola','hola2','hola3'],
            step: 0,
        };
    }

    changeProduct() {
        switch (this.state.step) {
            case 0:
                this.setState({ product: 'tu pecera' });
                break;
            case 1:
                this.setState({ product: 'el agua' });
                break;
            case 2:
                this.setState({ product: 'el suelo' });
                break;
            case 3:
                this.setState({ product: 'las plantas' });
                break;
            default:
                break;
        }
    }

    renderStep() {
        switch (this.state.step) {
            case 0:
                if (this.state.items) {
                    return this.state.items.map(item => {
                        return (
                            <p>{item}</p>
                        );
                    });
                }
                return null;
                break;
            case 1:
                if (this.state.items) {
                    return this.state.items.map(item => {
                        return (
                            <p>{item}</p>
                        );
                    });
                }
                return null;
                break;
            case 2:
                if (this.state.items) {
                    return this.state.items.map(item => {
                        return (
                            <p>{item}</p>
                        );
                    });
                }
                return null;
                break;
            case 3:
                if (this.state.items) {
                    return this.state.items.map(item => {
                        return (
                            <p>{item}</p>
                        );
                    });
                }
                return null;
                break;
            default:
                break;
        }
    }

    render () {
        return (
            <main className="site-main page-spacing">
                <div className="container-fluid no-padding">
                    <div className="section-padding"></div>
                    <div className="container">
                        <div className="section-header">
                            <Tabs className="tabContainer" selectedIndex={this.state.step} onSelect={step => { this.setState({ step }); this.changeProduct.bind(this); }}>
                                <TabList>
                                    <Tab className="tabs">1</Tab>
                                    <Tab className="tabs">2</Tab>
                                    <Tab className="tabs">3</Tab>
                                    <Tab className="tabs">4</Tab>
                                </TabList>
                                <TabPanel>
                                </TabPanel>
                                <TabPanel>
                                </TabPanel>
                                <TabPanel>
                                </TabPanel>
                                <TabPanel>
                                </TabPanel>
                            </Tabs>
                        </div>
                        <div className="row">
                            <div className="col-md-3">
                                <div className="section-header">
                					<h2>Elige {this.state.product}:</h2>
                				</div>
                                <div className="itemContainer">
                                    {this.renderStep()}
                                </div>
                            </div>
                            <div className="col-md-9">
                                <div className="section-header">
                                </div>
                                <div>
                                    <img src={require('./images/slider/8.jpg')} />
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="section-padding"></div>
                </div>
            </main>
        );
    }
}

export { Quoting };
