import React, { Component } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import { Header, Loading, Footer } from './Common';
import { Home } from './Home';
import Quoting from './Quoting/Quoting';
import { Contact } from './Contact';
import { Gallery } from './Gallery';
import './index.css';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            menuOption: 0,
        };
    }

    renderContent() {
        if (this.state.menuOption) {
            switch (this.state.menuOption) {
                case 0:
                    return (
                        <Home />
                    );
                    break;
                case 1:
                    return (
                        <Quoting />
                    );
                    break;
                case 2:
                    return (
                        <Gallery />
                    );
                    break;
                case 3:
                    return (
                        <Contact />
                    );
                    break;
            }
        }
    }

    render () {
        return (
            <div>
                <Header />
                <div className="menu-block container-fluid no-pdding">
                    <div className="container">
                        <nav className="navbar ow-navigation">
                            <div className="navbar-header">
                                <button type="button" className="navbar-toggle collapsed" data-toggle="collapse" data-target="#navbar" aria-expanded="false" aria-controls="navbar">
                                    <span className="sr-only">Toggle navigation</span>
                                    <span className="icon-bar"></span>
                                    <span className="icon-bar"></span>
                                    <span className="icon-bar"></span>
                                </button>
                                <a className="navbar-brand" href="index.html" title="Logo">MI PECERA</a>
                            </div>
                            <div id="navbar" className="navbar-collapse collapse">
                                <ul className="nav navbar-nav">
                                    <Tabs className="nav-tabContainer" selectedTabClassName="nav-tab-selected" selectedIndex={this.state.menuOption} onSelect={(tab) => this.setState({ menuOption: tab })}>
                                        <TabList>
                                            <Tab className="nav-tabs"><li><a>Inicio</a></li></Tab>
                                            <Tab className="nav-tabs"><li><a>Cotizador</a></li></Tab>
                                            <Tab className="nav-tabs"><li><a>Galer√≠a</a></li></Tab>
                                            <Tab className="nav-tabs"><li><a>Contacto</a></li></Tab>
                                        </TabList>
                                        <TabPanel>
                                        </TabPanel>
                                        <TabPanel>
                                        </TabPanel>
                                        <TabPanel>
                                        </TabPanel>
                                        <TabPanel>
                                        </TabPanel>
                                    </Tabs>
                                </ul>
                            </div>
                        </nav>
                    </div>
                </div>
                <Loading />
                {this.renderContent()}
                <Footer />
            </div>
        );
    }
}

export default App;
