import React, { Component } from 'react';
import axios from 'axios';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import './quote.css';

class Quoting extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fishBowls: [],
            water: ['dulce', 'salada'],
            product: 'tu pecera',
            items: ['hola','hola2','hola3'],
            step: 0,
        };
    }

    componentWillMount() {
        //get fishBowls
        axios.get(`http://mipecera.kimosolutions.com/api/step/1`, {
            headers: {
                "Content-Type": "application/json",
                "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
            }
        }).then(response  => response.data.map(fishBowls => this.setState({ fishBowls: [...this.state.fishBowls, fishBowls] })));

        //get water
        axios.get(`http://mipecera.kimosolutions.com/api/step/2`, {
            headers: {
                "Content-Type": "application/json",
                "Authorization": "&yBR31Z6a8jgppw6oK9si|If~nLiT{xdq)JeHS@qO/I<~7bxG(Uv25cih4m4bsK"
            }
        }).then(response  => response.data.map(water => this.setState({ water: [...this.state.water, water] })));
    }

    changeProduct() {
        switch (this.state.step) {
            case 0:
                this.setState({ product: 'tu pecera' });
                break;
            case 1:
                this.setState({ product: 'el agua' });
                break;
            case 2:
                this.setState({ product: 'el suelo' });
                break;
            case 3:
                this.setState({ product: 'las plantas' });
                break;
            default:
                break;
        }
    }

    renderStep() {
        switch (this.state.step) {
            case 0:
                if (this.state.fishBowls) {
                    return this.state.fishBowls.map(item => {
                        return (
                            <div className="item">
                                <div className="row">
                                    <div className="col-md-6">
                                        <img src={item.image} alt={item.code}/>
                                    </div>
                                    <div className="col-md-6">
                                        <p><strong>Capacidad:</strong> {item.capacity}</p>
                                        <p><strong>Altura:</strong> {item.height}</p>
                                        <p><strong>Largo:</strong> {item.width}</p>
                                        <p><strong>Ancho:</strong> {item.depth}</p>
                                    </div>
                                </div>
                            </div>
                        );
                    });
                }
                return null;
                break;
            case 1:
                if (this.state.water) {
                    return this.state.water.map(item => {
                        return (
                            <div>{item}</div>
                        );
                    });
                }
                return null;
                break;
            case 2:
                return (
                    <div>
                        <div className="item">
                            <div className="row">
                                <div className="col-md-6">
                                    <img src={water[0].image[0]} alt={water[0].type}/>
                                </div>
                                <div className="col-md-6">
                                    <p><strong>{water[1].type}</strong></p>
                                </div>
                            </div>
                        </div>
                        <div className="item">
                            <div className="row">
                                <div className="col-md-6">
                                    <img src={water[1].image[0]} alt={water[1].type}/>
                                </div>
                                <div className="col-md-6">
                                    <p><strong>{water[1].type}</strong></p>
                                </div>
                            </div>
                        </div>
                    </div>
                );
                break;
            case 3:
                if (this.state.items) {
                    return this.state.items.map(item => {
                        return (
                            <p>{item}</p>
                        );
                    });
                }
                return null;
                break;
            default:
                break;
        }
    }

    render () {
        return (
            <main className="site-main page-spacing">
                <div className="container-fluid no-padding">
                    <div className="container">
                        <div className="section-header">
                            <Tabs className="tabContainer" selectedIndex={this.state.step} onSelect={step => { this.setState({ step }); this.changeProduct(); }}>
                                <TabList>
                                    <Tab className="tabs">1</Tab>
                                    <Tab className="tabs">2</Tab>
                                    <Tab className="tabs">3</Tab>
                                    <Tab className="tabs">4</Tab>
                                </TabList>
                                <TabPanel>
                                </TabPanel>
                                <TabPanel>
                                </TabPanel>
                                <TabPanel>
                                </TabPanel>
                                <TabPanel>
                                </TabPanel>
                            </Tabs>
                        </div>
                        <div className="row">
                            <div className="col-md-3">
                                <div className="itemContainer">
                                    <h2>Elige {this.state.product}:</h2>
                                    <div>
                                        {this.renderStep()}
                                    </div>
                                </div>
                            </div>
                            <div className="col-md-9">
                                <div>
                                    <img src={require('./images/slider/8.jpg')} />
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="section-padding"></div>
                </div>
            </main>
        );
    }
}

export { Quoting };
